entry.value:
def flowFiles = session.get(1000)
flowFiles.each { reqFlowFile ->
	def PG_NAME = reqFlowFile.getAttriobute("PG_NAME");
	def PG_UUID = reqFlowFile.getAttriobute("PG_UUID");
	def Authorization = reqFlowFile.getAttriobute("Authorization");
	def resFlowFile = session.create();
	resFlowFile = session.putAttribute(resFlowFile, "PG_NAME", PG_NAME);
	resFlowFile = session.putAttribute(resFlowFile, "PG_UUID", PG_UUID);
	resFlowFile = session.putAttribute(resFlowFile, "Authorization", Authorization);
	session.transfer(resFlowFile, REL_SUCCESS)
}
session.remove(flowFiles)
